import React from "react";
import Chart from "../utils/chart";
import PropTypes from "prop-types";
import axios from 'axios';
import {
  Container,
  Row,
  Col,
  FormSelect,
  Card,
  CardHeader,
  CardBody,
  CardFooter
} from "shards-react";
import PageTitle from "./../components/common/PageTitle";
import {emojify} from 'react-emojione';
import SmallStats from "../components/common/SmallStats";

import "./cards.css"

import RangeDatePicker from "../components/common/RangeDatePicker";

var CanvasJSReact = require('./canvasjs/canvasjs.react');
var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

const options = {
  // convertShortnames: true,
  // convertUnicode: true,
  // convertAscii: true,
  style: {
      // backgroundImage: 'url("/path/to/your/emojione.sprites.png")',
      height: 150,
      margin: 2,
  },
  // this click handler will be set on every emoji
  // onClick: event => alert(event.target.title)
};

const datasets= [
  {
    label: "Today",
    fill: "start",
    borderWidth: 1.5,
    backgroundColor: "rgba(0, 184, 216, 0.1)",
    borderColor: "rgb(0, 184, 216)",
    data: [1, 2, 1, 3, 5, 4, 7]
  }
]
const attrs= { md: "6", sm: "6" };

class UserProfileLite extends React.Component{
  constructor(props) {
    super(props);
    this.canvasRefLine = React.createRef();
    this.state={
      title: "Users Statistics",
      GenderData: { 
        datasets: [
          {
            hoverBorderColor: "#ffffff",
            label : "Male/Female Count",
            // order: 4,
            data: [],
            backgroundColor: [
              "rgba(0,123,255,0.9)",
              "rgba(0,123,255,0.5)"            ]
          }
        ],
        labels: ["Male", "Female"]
      },

      AgeData: {
        datasets: [
          {
            hoverBorderColor: "#ffffff",
            label : "Gender Count",
            // order: 4,
            data: [24, 22, 70, 34, 54, 32, 11, 5, 0],
            backgroundColor: [
              "rgba(0,123,255,0.9)",
              "rgba(0,123,255,0.8)",
              "rgba(0,123,255,0.7)",
              "rgba(0,123,255,0.6)",
              "rgba(0,123,255,0.5)",
              "rgba(0,123,255,0.4)",
              "rgba(0,123,255,0.3)",
